const readlineSync = require("readline-sync")
const name = readlineSync.question("Welcome to the Escape Room. In order to escape the room, you need to find the key in the room and then open the door. There's also a hole in the wall in the room. If you put your hand in the hole, you die. What is your name? ");
        
console.log("welcome" + name + "to the game")

var choices = ["Put hand in hole", "Find the key", "Open the door"]

var operation = readlineSync.keyInSelect(choices,"Please select an option:")

let isAlive = true 
// var results = ["You're dead","You escaped!", "You got the key"]


while (isAlive === true) {

    if(choices[operation] === "Put hand in hole") {
        isAlive = false
        console.log("You are dead")
    } else if (choices[operation] === "Find the key") {
        console.log("You found the key")
        const secondChoice = readlineSync.keyInYN("You have the key..Would you like to escape? Yes or no?")
        if (secondChoice === true) {
            console.log("You escaped!")
            break
        } else if (secondChoice === false) {
            console.log("You're dead!?")
            isAlive = false
        }
    } else if (choices[operation] === "Open the door") {
        isAlive = false
        console.log("You didn't find the key..play again")
    }



}


